mapper.select:
SELECT course_number courseNumber FROM courses
WHERE is_active = true and academy_location =#{academyLocation}
and (false = #{excludeExpired} OR course_end_date > CURDATE())

mapper.update:
update courses set manager_id = #{managerId}
, course_number = #{courseNumber}
, academy_location = #{academyLocation}
, course_name = #{courseName}
, course_start_date = #{courseStartDate}
, course_end_date = #{courseEndDate}
, subject = #{subject}
, course_type = #{courseType}
, total_training_days = #{totalTrainingDays}
, training_hours_of_date = #{trainingHoursOfDate}
, professor_name = #{professorName}
, max_students = #{maxStudents} where course_seq = #{courseSeq} and is_active = true;

mapper.select:
SELECT course_number courseNumber
FROM courses
WHERE DATE_FORMAT(course_end_date, '%Y') = #{courseEndYear}

SELECT course_seq
FROM courses
WHERE course_number=#{courseNumber}
